/// <summary>
/// Finds the Cash register fiscal transaction for the given customer transaction.
/// </summary>
/// <param name="_custTrans">
/// The customer transaction.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// The Cash register fiscal transaction.
/// </returns>
public server static CashRegisterFiscalTrans_W findVendTrans(
    VendTrans _vendTrans,
    boolean   _forUpdate = false)
{
    CashRegisterFiscalTrans_W fiscalTrans;

    if (_forUpdate)
    {
        fiscalTrans.selectForUpdate(true);
    }

    if (_vendTrans)
    {
        fiscalTrans = CashRegisterFiscalTrans_W::findVoucherDate(_vendTrans.Voucher, _vendTrans.TransDate, _forUpdate);
    }

    return fiscalTrans;
}
