Method.Source:
/// <summary>
/// Determines if <c>CustVendPDCRegister</c> table record exists for a specified <c>VendTrans</c> table record.
/// </summary>
/// <param name="_vendTransRecId">
/// The record ID of the <c>VendTrans</c> table record.
/// </param>
/// <returns>
/// true if a record is found; otherwise, false.
/// </returns>
public static boolean existsForVendTrans(RefRecId _vendTransRecId)
{
    CustVendPDCRegister custVendPDCRegister;
    LedgerJournalTrans ledgerJournalTrans;

    Debug::assert(_vendTransRecId != 0);

    select firstOnly RecId from custVendPDCRegister
        exists join Recid from ledgerJournalTrans
            where custVendPDCRegister.LedgerJournalTrans == ledgerJournalTrans.RecId
                && ledgerJournalTrans.VendTransId == _vendTransRecId;

    return custVendPDCRegister.RecId != 0;
}
